["actions"] = {
["start"] = {
},
["init"] = {
["custom"] = "local objectives = {\n    {name=\"Repeatable Treasures/Dirt (Alchemy)\", quests={83253, 83255}, optionKey=\"alchemy\", skillID=171},\n    {name=\"Repeatable Treasures/Dirt (Blacksmithing)\", quests={83256, 83257}, optionKey=\"blacksmithing\", skillID=164},\n    {name=\"Repeatable Treasures/Dirt (Enchanting)\", quests={83258, 83259}, optionKey=\"enchanting\", skillID=333},\n    {name=\"Repeatable Treasures/Dirt (Engineering)\", quests={83260, 83261}, optionKey=\"engineering\", skillID=202},\n    {name=\"Repeatable Treasures/Dirt (Inscription)\", quests={83262, 83264}, optionKey=\"inscription\", skillID=773},\n    {name=\"Repeatable Treasures/Dirt (Jewelcrafting)\", quests={83265, 83266}, optionKey=\"jewelcrafting\", skillID=755},\n    {name=\"Repeatable Treasures/Dirt (Leatherworking)\", quests={83267, 83268}, optionKey=\"leatherworking\", skillID=165},\n    {name=\"Repeatable Treasures/Dirt (Tailoring)\", quests={83269, 83270}, optionKey=\"tailoring\", skillID=197},\n    \n    {name=\"Deepgrove Rose (Herbalism)\", quests={81416, 81417, 81418, 81419, 81420, 81421}, optionKey=\"herbalism\", skillID=182},\n    {name=\"Slab of Slate/Erosion Polished Stone (Mining)\", quests={83050, 83051, 83052, 83053, 83054, 83049}, optionKey=\"mining\", skillID=186},\n    {name=\"Toughened Tempest Pelt/Abyssal Fur (Skinning)\", quests={81459, 81460, 81461, 81462, 81463, 81464}, optionKey=\"skinning\", skillID=393},\n    {name=\"Fleeting Arcane Manifestation/Gleaming Telluric Crystal (Disenchanting)\", quests={84290, 84291, 84292, 84293, 84294, 84295}, optionKey=\"enchanting\", skillID=333},\n    \n    {name=\"Algari Treatise (Alchemy)\", quests={83725}, optionKey=\"alchemy\", skillID=171},\n    {name=\"Algari Treatise (Blacksmithing)\", quests={83726}, optionKey=\"blacksmithing\", skillID=164},\n    {name=\"Algari Treatise (Enchanting)\", quests={83727}, optionKey=\"enchanting\", skillID=333},\n    {name=\"Algari Treatise (Engineering)\", quests={83728}, optionKey=\"engineering\", skillID=202},\n    {name=\"Algari Treatise (Herbalism)\", quests={83729}, optionKey=\"herbalism\", skillID=182},\n    {name=\"Algari Treatise (Inscription)\", quests={83730}, optionKey=\"inscription\", skillID=773},\n    {name=\"Algari Treatise (Jewelcrafting)\", quests={83731}, optionKey=\"jewelcrafting\", skillID=755},\n    {name=\"Algari Treatise (Leatherworking)\", quests={83732}, optionKey=\"leatherworking\", skillID=165},\n    {name=\"Algari Treatise (Mining)\", quests={83733}, optionKey=\"mining\", skillID=186},\n    {name=\"Algari Treatise (Skinning)\", quests={83734}, optionKey=\"skinning\", skillID=393},\n    {name=\"Algari Treatise (Tailoring)\", quests={83735}, optionKey=\"tailoring\", skillID=197},\n    \n    --{name=\"Dornogal Profession Quests (Alchemy)\", quests={}, optionKey=\"alchemy\", skillID=171, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Blacksmithing)\", quests={}, optionKey=\"blacksmithing\", skillID=164, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Engineering)\", quests={}, optionKey=\"engineering\", skillID=202, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Inscription)\", quests={}, optionKey=\"inscription\", skillID=773, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Jewelcrafting)\", quests={}, optionKey=\"jewelcrafting\", skillID=755, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Leatherworking)\", quests={}, optionKey=\"leatherworking\", skillID=165, useWorldState=true},\n    --{name=\"Dornogal Profession Quests (Tailoring)\", quests={}, optionKey=\"tailoring\", skillID=197, useWorldState=true},\n    {name=\"Dornogal Profession Quests (Enchanting)\", quests={84084, 84085, 84086}, optionKey=\"enchanting\", skillID=333, unique=true},\n    {name=\"Dornogal Profession Quests (Herbalism)\", quests={82965, 82958, 82916, 82962, 82970}, optionKey=\"herbalism\", skillID=182, unique=true},\n    {name=\"Dornogal Profession Quests (Mining)\", quests={83105, 83106, 83104, 83102, 83103}, optionKey=\"mining\", skillID=186, unique=true},\n    {name=\"Dornogal Profession Quests (Skinning)\", quests={83098, 82993, 82992, 83100, 83097}, optionKey=\"skinning\", skillID=393, unique=true},\n    \n    {name=\"Crafting Services Requested (Blacksmithing)\", quests={84127}, optionKey=\"blacksmithing\", skillID=164},\n    {name=\"Crafting Services Requested (Engineering)\", quests={84128}, optionKey=\"engineering\", skillID=202},\n    {name=\"Crafting Services Requested (Inscription)\", quests={84129}, optionKey=\"inscription\", skillID=773},\n    {name=\"Crafting Services Requested (Jewelcrafting)\", quests={84130}, optionKey=\"jewelcrafting\", skillID=755},\n    {name=\"Crafting Services Requested (Leatherworking)\", quests={84131}, optionKey=\"leatherworking\", skillID=165},\n    {name=\"Crafting Services Requested (Tailoring)\", quests={84132}, optionKey=\"tailoring\", skillID=197},\n    {name=\"Crafting Services Requested (Alchemy)\", quests={84133}, optionKey=\"alchemy\", skillID=171},\n    --{name=\"Crafting Services Requested (Enchanting)\", quests={84134}, optionKey=\"enchanting\", skillID=333},\n}\n\n-- This returns true if at least one of the required quests is currently active.\nlocal is_active = function(objective)\n    if not aura_env.config[objective.optionKey] then\n        return false\n    end\n    if not objective.required_quests then\n        return true\n    end\n    for _, q in ipairs(objective.required_quests) do\n        if C_TaskQuest.GetQuestTimeLeftSeconds(q) or C_QuestLog.IsQuestFlaggedCompleted(q) then\n            return true\n        end\n    end\nend\n\n-- This returns the number of quests that are not complete.\nlocal remaining_quests = function(objective)\n    local c = #objective.quests\n    if objective.useWorldState then \n        c = 0\n        for _, q in ipairs(objective.quests) do\n            if C_TaskQuest.GetQuestTimeLeftSeconds(q) then\n                c = c + 1\n            end\n        end\n    elseif objective.unique then\n        c = 1\n        for _, q in ipairs(objective.quests) do\n            if C_QuestLog.IsQuestFlaggedCompleted(q) then\n                c = c - 1\n            end\n        end\n    else\n        for _, q in ipairs(objective.quests) do\n            if C_QuestLog.IsQuestFlaggedCompleted(q) then\n                c = c - 1\n            end\n        end\n    end\n    return c\nend\n\nlocal profession_known = function(objective)\n    local prof1, prof2 = GetProfessions()\n    local prof1SkillID, prof2SkillID, _\n    if (prof1 ~= nil) then\n        _,_,_,_,_,_,prof1SkillID = GetProfessionInfo(prof1)\n    end\n    if (prof2 ~= nil) then\n        _,_,_,_,_,_,prof2SkillID = GetProfessionInfo(prof2)\n    end\n    if (objective.skillID == prof1SkillID) or (objective.skillID == prof2SkillID) then\n        return true\n    else\n        if not aura_env.config[\"automaticDetection\"] then\n            return true\n        else\n            return false\n        end\n    end\nend\n\naura_env.update_display = function()\n    aura_env.text = nil\n    for _, objective in ipairs(objectives) do\n        if is_active(objective) and profession_known(objective) then\n            local c = remaining_quests(objective)\n            if c > 0 then\n                if aura_env.text then\n                    aura_env.text = aura_env.text .. \"\\n\" .. objective.name .. \": \" .. c\n                else\n                    aura_env.text = objective.name .. \": \" .. c\n                end\n            end\n        end\n    end\nend\n\naura_env.update_display()",
